type Some<T> = {
    _tag: 'some';
    value: T;
};
type None = {
    _tag: 'none';
};
/**
 * @desc 성공이냐? 실패냐? 그것이 문제로다
 * @type {Some<T>} 성공
 * @type {None} 실패
 */
export type Option<T> = Some<T> | None;
export declare const some: <T>(value: T) => Some<T>;
export declare const none: () => None;
export declare const isSome: <T>(o: Option<T>) => o is Some<T>;
export declare const isNone: (o: Option<unknown>) => o is None;
export declare const castOption: <T>(value: T, conditionFn: (v: T) => boolean) => Option<T>;
export {};
